INSERT INTO public.card (id, latitude, longitude, age, alias, color, gender, created_at, description, updated_at, breed_id, person_id, picture_id) VALUES (52, 60.05579, 30.426836, 8, 'Джеймс', 'Рыжий', 'самец', '2018-07-27 13:23:51.434000', '26.07.2018 пропал наш Джеймс, вельш-корги. Испугался звука фейерверка и вырвался из ошейника. Убежал вечером, в районе 18:15, по адресу д. 9 ул. Шувалова (Девяткино). Приметы: ярко-красный ошейник, адресник, на левой задней лапе большое белое пятно. Откликается на кличку Джекки. Всех, кто видел его или знает что-либо о его местоположении, просим сразу сообщить нам. Нашедшим вознаграждение гарантируется.', '2018-07-27 13:23:51.434000', 24, 2, 51);
INSERT INTO public.card (id, latitude, longitude, age, alias, color, gender, created_at, description, updated_at, breed_id, person_id, picture_id) VALUES (54, 60.145916, 30.208992, 1, 'Джус', 'Коричневый', 'самка', '2018-07-27 13:27:48.571000', '26.07.2018 пропала кошка породы азиатский табби.  Выпрыгнула из окна (живем на первом этаже) вечером, в районе 20:00, по адресу д.6 ул. Школьной (Сертолово). Приметы: ярко-красный ошейник, адресник, на левой задней лапе большое белое пятно. Откликается на кличку Джус. Всех, кто видел его или знает что-либо о ее местоположении, просим сразу сообщить нам. Нашедшим вознаграждение гарантируется.', '2018-07-27 13:27:48.571000', 51, 2, 53);
INSERT INTO public.card (id, latitude, longitude, age, alias, color, gender, created_at, description, updated_at, breed_id, person_id, picture_id) VALUES (56, 59.946224, 30.258608, 8, 'Штрудель', 'бело-коричневый', 'самец', '2018-07-27 13:33:42.944000', 'Пропал бассет-хаунд. Убежал 23.08.2018 вечером, от д.95, 17 линии ВО. Испугался звука фейерверка и вырвался из ошейника.  Приметы: коричневый ошейник, адресник, прихрамывает на переднюю правую лапу. Откликается на кличку Штрудель. Очень общительный и дружелюбный. Всех, кто видел его или знает что-либо о его местоположении, просим сразу сообщить нам. Нашедшим вознаграждение гарантируется.', '2018-07-27 13:33:42.944000', 16, 2, 55);
INSERT INTO public.card (id, latitude, longitude, age, alias, color, gender, created_at, description, updated_at, breed_id, person_id, picture_id) VALUES (50, 59.89575, 30.412996, 5, 'Бобби', 'Бело-коричневый', 'самец', '2018-07-27 13:21:30.831000', 'Пропал бигль. Откликается на кличку Бобби. Испугался проезжающей мимо машины и вырвался из ошейника. Убежал 26.07.2018 вечером, в районе 18:15, по адресу д.31 ул. Седова (Елизаровская). Приметы: ярко-зелёный ошейник, адресник, на левой задней лапе большое белое пятно. Всех, кто видел его или знает что-либо о его местоположении, просим сразу сообщить нам. Нашедшим вознаграждение гарантируется.
